
import { Component, OnInit } from '@angular/core';
import { CartService } from 'src/app/services/cart.service';

interface MenuItem {
  name: string;
  category: string;
  description: string;
  price: number;
  image: string;
}

@Component({
  selector: 'app-menu',
  templateUrl: './menu.component.html',
  styleUrls: ['./menu.component.css']
})
export class MenuComponent implements OnInit {

  menu: MenuItem[] = [
    {name:'Classic Beef Burger',category:'Burgers',description:'Juicy beef patty.',price:120,image:'data:image/jpeg;base64,'},
    {name:'Chicken Burger',category:'Burgers',description:'Grilled chicken.',price:110,image:'data:image/jpeg;base64,'},
    {name:'Margherita Pizza',category:'Pizza',description:'Tomato & cheese.',price:180,image:'data:image/jpeg;base64,'},
    {name:'Spaghetti Carbonara',category:'Pasta',description:'Creamy pasta.',price:150,image:'data:image/jpeg;base64,'},
    {name:'Salmon Sushi Roll',category:'Sushi',description:'Fresh salmon.',price:200,image:'data:image/jpeg;base64,'},
    {name:'Chocolate Cake',category:'Drinks',description:'Rich chocolate.',price:60,image:'data:image/jpeg;base64,'}
  ];

  categories: string[] = ['All', 'Burgers', 'Pizza', 'Pasta', 'Sushi', 'Drinks'];
  filteredMenu: MenuItem[] = [];
  activeCategory = 'All';

  constructor(private cartService: CartService) {}

  ngOnInit(): void {
    this.filteredMenu = this.menu;
  }

  filterCategory(cat: string) {
    this.activeCategory = cat;
    this.filteredMenu =
      cat === 'All'
        ? this.menu
        : this.menu.filter(i => i.category === cat);
  }

  addToCart(item: MenuItem) {
    this.cartService.add(item);
    alert('Added to cart');
  }
}
